mapper.select:
select count(chat_no) cnt from memo_chat
where mno_read = #{mno}
and read_yn = 1 
and room_no = #{room_no}

select mno, mni, mpho_path
from member


	where mno = #{mno_to}


	where mno = #{mno_from}

select mpho_path
from member
where mno = #{mno}

mapper.update:
update memo_chat set read_yn = 0


	where room_no = #{room_no}
	and read_yn = 1
	and mno_read = #{mno}


	where mno_ins = #{mno}
	and read_yn = 1
	and mno_read = #{other_mno}
