select:
select member set mdes = #{mdes}
				, mdes = #{mdes}
where mno = #{mno}

update:
update member set mni = #{mni}
			, mdes = #{mdes}

				, mpho = #{mpho}
				, mpho_path = #{mpho_path}

where mno = #{mno}

update member set mpwd = #{mpwd}
				, email = #{email}
				, tel = #{tel}
where mno = #{mno}
