mapper.select:
select mno, email, mpwd, mni, tel, mpho, mpho_path, mdes
from member
where mno = #{mno}

mapper.update:
update member set mni = #{mni}
			, mdes = #{mdes}

				, mpho = #{mpho}
				, mpho_path = #{mpho_path}

where mno = #{mno}

update member set mpwd = #{mpwd}
				, email = #{email}
				, tel = #{tel}
where mno = #{mno}
